import React, {useContext, useEffect, useState} from "react";
import {
	Row,
	Col,
	Container,
	CardBody,
	Modal,
	CardTitle,
	CardImg,
	Button,
	CardText,
	ModalHeader,
	ModalBody,
	FormGroup,
	ModalFooter,
} from "reactstrap";
import {
	AvForm,
	AvField,
	AvInput,
	AvSelect,
	AvRadioGroup,
	AvRadio,
} from "availity-reactstrap-validation";
//Import Images
import img2 from "../../assets/images/small/img-2.jpg";

const GetUsers = ({item}) => {
	const [modal, setModal] = useState(false);
	const toggle = () => setModal(!modal);

	/*Creacion del objeto usuario */
	const [usuario, actualizarUsuario] = useState({
		_id: "",
		name: "",
		surname: "",
		age: "",
		gender: "",
		confidenceLevels: "",
		image: "",
	});

	// Lee los contenidos del input
	const onChange = (e) => {
		actualizarUsuario({
			...usuario,
			[e.target.name]: e.target.value,
		});
	};

	const onSubmitUsuario = (e) => {
		//actualizarFace(usuario);
		toggle();
	};
	{
		/*onClick={() => eliminarUsuario(item._id)}}*/
	}
	return (
		<div className="wrapper">
			<Row>
				<Container>
					<Col md="6">
						<CardTitle>
							<div className={"user-img align-self-center mr-3 " + chat.status}>
								<img
									src={chat.image}
									className="rounded-circle avatar-xs"
									alt=""
								/>
								<span className="user-status"></span>
							</div>
							<h5>
								{item.name} {item.surname}
							</h5>
						</CardTitle>
						<CardText>
							<strong>Edad:</strong> {item.age} - <strong>Sexo:</strong>{" "}
							{item.gender}
						</CardText>
					</Col>
					<Col md="6">
						<CardText>
							<Button
								color="primary"
								className=" waves-effect waves-light mr-2"
								onClick={toggle}>
								Editar
							</Button>

							<Button
								color="secondary"
								outline
								className=" waves-effect waves-light">
								Eliminar
							</Button>
						</CardText>
					</Col>
				</Container>
			</Row>
		</div>
	);
};

export default GetUsers;
