import React, {useState, useContext, useEffect} from "react";
import {Dropdown, DropdownToggle, DropdownMenu, DropdownItem} from "reactstrap";
import AuthContext from "../../../context/autenticacion/authContext";

//i18n
import {withNamespaces} from "react-i18next";
import {Link} from "react-router-dom";

// users
import avatar2 from "../../../assets/images/users/avatar-2.jpg";

const ProfileMenu = (props) => {
	const authContext = useContext(AuthContext);
	const {autenticado, usuario, usuarioAutenticado, cerrarSesion} = authContext;

	//Abrir y cerrar desplegable
	const [togglevar, setToggle] = useState(false);
	const toggle = () => setToggle(!togglevar);

	useEffect(() => {
		usuarioAutenticado();
	}, []);

	const cerrarSesionUser = () => {
		cerrarSesion();
		if (!autenticado) {
			props.history.push("/login");
		}
	};

	return (
		<React.Fragment>
			<Dropdown
				isOpen={togglevar}
				toggle={toggle}
				className="d-inline-block user-dropdown">
				<DropdownToggle
					tag="button"
					className="btn header-item waves-effect"
					id="page-header-user-dropdown">
					<img
						className="rounded-circle header-profile-user mr-1"
						src={avatar2}
						alt="Header Avatar"
					/>
					<span className="d-none d-xl-inline-block ml-1 text-transform">
						{usuario ? usuario.name : null} {usuario ? usuario.surname : null}
					</span>
					<i className="mdi mdi-chevron-down d-none ml-1 d-xl-inline-block"></i>
				</DropdownToggle>
				<DropdownMenu right>
					<DropdownItem href={`/profile/${usuario ? usuario._id : null}`}>
						<i className="ri-user-line align-middle mr-1"></i>{" "}
						{props.t("Profile")}
					</DropdownItem>

					<DropdownItem className="d-block" href="/settings">
						<i className="ri-settings-2-line align-middle mr-1"></i>{" "}
						{props.t("Settings")}
					</DropdownItem>

					<DropdownItem divider />
					<DropdownItem className="text-danger" onClick={cerrarSesionUser}>
						<i className="ri-shut-down-line align-middle mr-1 text-danger"></i>{" "}
						{props.t("Logout")}
					</DropdownItem>
				</DropdownMenu>
			</Dropdown>
		</React.Fragment>
	);
};

export default withNamespaces()(ProfileMenu);
