import React, {useContext, useEffect, useState} from "react";
import {
	Row,
	Col,
	CardBody,
	Modal,
	CardTitle,
	CardImg,
	Button,
	CardText,
	ModalHeader,
	ModalBody,
	FormGroup,
	ModalFooter,
} from "reactstrap";
import {
	AvForm,
	AvField,
	AvInput,
	AvSelect,
	AvRadioGroup,
	AvRadio,
} from "availity-reactstrap-validation";
//Import Images
import img2 from "../../assets/images/small/img-2.jpg";
import faceContext from "../../context/face/faceContext";

const GetUsers = ({face, eliminarUsuario}) => {
	const [modal, setModal] = useState(false);
	const toggle = () => setModal(!modal);

	const facesContext = useContext(faceContext);
	const {actualizarFace} = facesContext;

	/*Creacion del objeto usuario */
	const [usuario, actualizarUsuario] = useState({
		name: "",
		surname: "",
		age: "",
		gender: "",
		confidenceLevels: "",
		image: "",
	});

	// Lee los contenidos del input
	const onChange = (e) => {
		actualizarUsuario({
			...usuario,
			[e.target.name]: e.target.value,
		});
	};

	const onSubmitUsuario = (e) => {
		actualizarFace(usuario);
		//Reiniciar formulario
		actualizarUsuario({
			name: "",
			surname: "",
			age: "",
			gender: "",
			confidenceLevels: "",
			image: "",
		});
		toggle();
	};

	return (
		<Row className="no-gutters align-items-center">
			<Col md={3}>
				<CardImg className="img-fluid" src={img2} alt="Skote" />
			</Col>
			<Col md={9}>
				<CardBody>
					<CardTitle>
						<h5>
							{face.name} {face.surname}
						</h5>
					</CardTitle>
					<CardText>
						<strong>Edad:</strong> {face.age} - <strong>Sexo:</strong>{" "}
						{face.gender}
					</CardText>
					<CardText>
						<Button
							color="primary"
							className=" waves-effect waves-light mr-2"
							onClick={toggle}>
							Editar
						</Button>
						<Button
							color="secondary"
							outline
							className=" waves-effect waves-light"
							onClick={() => eliminarUsuario(face._id)}>
							Eliminar
						</Button>
					</CardText>
				</CardBody>
			</Col>
			<Modal isOpen={modal} centered={true} toggle={toggle}>
				<ModalHeader toggle={toggle}>Editar usuario</ModalHeader>
				<ModalBody>
					<AvForm
						className="needs-validation mt-3"
						onValidSubmit={onSubmitUsuario}>
						<Row>
							<Col md="6">
								<AvField
									name="name"
									label="Nombre"
									value={face.name}
									errorMessage="Introduce un nombre válido"
									className="form-control"
									onChange={onChange}
									validate={{required: {value: true}}}
								/>
							</Col>
							<Col md="6">
								<AvField
									name="surname"
									label="Apellido"
									value={face.surname}
									errorMessage="Introduce un apellido válido"
									onChange={onChange}
									className="form-control"
									validate={{required: {value: true}}}
								/>
							</Col>
						</Row>
						<AvField
							name="age"
							label="Edad"
							type="text"
							validate={{number: true}}
							errorMessage="Introduce una edad válida"
							className="form-control"
							onChange={onChange}
							required
						/>
						<AvRadioGroup
							inline
							label="Sexo"
							name="gender"
							required
							errorMessage="Selecciona el sexo">
							<AvRadio
								label="Masculino"
								value="Masculino"
								onChange={onChange}
							/>
							<AvRadio label="Femenino" value="Femenino" onChange={onChange} />
							<AvRadio label="Otro" value="Otro" onChange={onChange} />
						</AvRadioGroup>

						<AvField
							type="select"
							required
							label="Nivel de confianza"
							onChange={onChange}
							name="confidenceLevels"
							errorMessage="Selecciona el nivel de confianza al que pertenece el usuario">
							<option value="">Selecciona un nivel de confianza</option>
							<option value="60316459aea69a7200c32a64">1</option>
							<option value="603165309fc13d17bcbd7089">2</option>
							<option value="603167ef9fc13d17bcbd708a">3</option>
							<option value="603168839fc13d17bcbd708b">4</option>
						</AvField>
						<AvField
							type="file"
							id="image"
							name="image"
							errorMessage="La foto del rostro del usuario es obligatoria"
							onChange={onChange}
						/>

						<FormGroup>
							<Button
								color="primary"
								className=" waves-effect waves-light mr-2">
								Añadir
							</Button>
						</FormGroup>
					</AvForm>
				</ModalBody>
			</Modal>
		</Row>
	);
};

export default GetUsers;
