import React, {useContext, useEffect, useState} from "react";
import {
	Row,
	Col,
	CardBody,
	Modal,
	CardTitle,
	CardImg,
	Button,
	CardText,
	ModalHeader,
	ModalBody,
	FormGroup,
	ModalFooter,
} from "reactstrap";
import {
	AvForm,
	AvField,
	AvInput,
	AvSelect,
	AvRadioGroup,
	AvRadio,
} from "availity-reactstrap-validation";
//Import Images
import img2 from "../../assets/images/small/img-2.jpg";
import faceContext from "../../context/face/faceContext";

const GetUsers = ({item}) => {
	const [modal, setModal] = useState(false);
	const toggle = () => setModal(!modal);

	const facesContext = useContext(faceContext);
	const {obtenerFaces, faces} = facesContext;

	const [list, setList] = useState([]);
	useEffect(() => {
		obtenerFaces();
	}, []);

	/*Creacion del objeto usuario */
	const [usuario, actualizarUsuario] = useState({
		_id: "",
		name: "",
		surname: "",
		age: "",
		gender: "",
		confidenceLevels: "",
		image: "",
	});

	// Lee los contenidos del input
	const onChange = (e) => {
		actualizarUsuario({
			...usuario,
			[e.target.name]: e.target.value,
		});
	};

	const onSubmitUsuario = (e) => {
		//actualizarFace(usuario);
		toggle();
	};

	return (
		<div className="wrapper">
			<CardTitle>
				<h5>
					{item.name} {item.surname}
				</h5>
			</CardTitle>
			<CardText>
				<strong>Edad:</strong> {item.age} - <strong>Sexo:</strong> {item.gender}
			</CardText>
			<CardText>
				<Button
					color="primary"
					className=" waves-effect waves-light mr-2"
					onClick={toggle}>
					Editar
				</Button>
				<Button color="secondary" outline className=" waves-effect waves-light">
					Eliminar
				</Button>
			</CardText>
		</div>
	);
};

export default GetUsers;
