import React, {useReducer} from "react";
import AuthContext from "./authContext";
import AuthReducer from "./authReducer";

import clienteAxios from "../config/axios";
import tokenAuth from "../config/token";

import {
	REGISTRO_EXITOSO,
	REGISTRO_ERROR,
	OBTENER_USUARIO,
	LOGIN_EXITOSO,
	LOGIN_ERROR,
	CERRAR_SESION,
} from "../types";

const AuthState = (props) => {
	const initialState = {
		token: localStorage.getItem("token"),
		autenticado: null,
		usuario: null,
		mensaje: null,
		cargando: true,
	};

	const [state, dispatch] = useReducer(AuthReducer, initialState);

	const registrarUsuario = async (body) => {
		try {
			const respuesta = await clienteAxios.post("/api/registro", body);

			dispatch({
				type: REGISTRO_EXITOSO,
				payload: respuesta.data.token,
			});
			usuarioAutenticado();
		} catch (error) {
			// console.log(error.response.data.msg);
			const alerta = {
				message: "Hubo un error al registrar al usuario",
				categoria: "alerta-error",
			};

			dispatch({
				type: REGISTRO_ERROR,
				payload: alerta,
			});
		}
	};

	// Retorna el usuario autenticado
	const usuarioAutenticado = async () => {
		const token = localStorage.getItem("token");

		if (token) {
			tokenAuth(token);
		}

		try {
			const respuesta = await clienteAxios.get("/api/auth-user");
			dispatch({
				type: OBTENER_USUARIO,
				payload: respuesta.data.usuario,
			});
		} catch (error) {
			dispatch({
				type: LOGIN_ERROR,
			});
		}
	};

	// Cuando el usuario inicia sesi贸n
	const iniciarSesion = async (datos) => {
		let payload = datos;
		payload.gethash = true;
		try {
			const respuesta = await clienteAxios.post("/api/login", payload);
			console.log(respuesta);
			dispatch({
				type: LOGIN_EXITOSO,
				payload: respuesta.data.token,
			});
			usuarioAutenticado();
		} catch (error) {
			/*
			console.log(error.response.data.msg);
            const alerta = {
                msg: error.response.data.msg,
                categoria: 'alerta-error'
            }

            dispatch({
                type: LOGIN_ERROR,
                payload: alerta
			})
			*/
		}
	};

	//Cerrar sesion del usuario
	// Cierra la sesi贸n del usuario
	const cerrarSesion = () => {
		dispatch({
			type: CERRAR_SESION,
		});
	};

	return (
		<AuthContext.Provider
			value={{
				token: state.token,
				autenticado: state.autenticado,
				usuario: state.usuario,
				mensaje: state.mensaje,
				cargando: state.cargando,
				registrarUsuario,
				iniciarSesion,
				usuarioAutenticado,
				cerrarSesion,
			}}>
			{props.children}
		</AuthContext.Provider>
	);
};
export default AuthState;
