import React, {useState, useContext} from "react";
import {FormGroup, Label, Input, FormText} from "reactstrap";
import {AvForm, AvField, AvInput} from "availity-reactstrap-validation";
import faceContext from "../../context/face-confidenceLevels/faceContext";

const AddUser = () => {
	// Obtener el state del formulario
	const facesContext = useContext(faceContext);
	const {agregarFace, mostrarError} = facesContext;

	// State para Proyecto
	const [face, guardarFace] = useState({
		name: "",
		surname: "",
		age: "",
		gender: "",
		confidenceLevels: "",
	});

	// Extraer nombre de face
	const {fullname, surname, age, gender, confidenceLevels} = face;

	// Lee los contenidos del input
	const actualizarState = (e) => {
		guardarFace({
			...face,
			[e.target.fullname]: e.target.value,
		});
	};
	// Cuando el usuario envia un face
	const onSubmitFace = (e) => {
		e.preventDefault();

		// Validar el face
		if (fullname === "") {
			mostrarError();
			return;
		}

		// agregar al state
		agregarFace(face);

		// Reiniciar el form
		guardarFace({
			nombre: "",
		});
	};

	return (
		<React.Fragment>
			<AvForm className="needs-validation mt-3">
				<FormGroup>
					<Label for="fullname">Nombre completo</Label>
					<AvField
						name="fullname"
						placeholder="Nombre"
						type="text"
						errorMessage="Introduce un nombre vÃ¡lido"
						className="form-control"
						validate={{required: {value: true}}}
						id="fullname"
						onChange={actualizarState}
					/>
				</FormGroup>
				<FormGroup>
					<Label for="age">Edad</Label>
					<Input type="text" name="age" id="age" />
				</FormGroup>
				<Label for="gender">Sexo</Label>
				<FormGroup check>
					<Label check>
						<Input type="radio" name="gender" defaultChecked /> Masculino
					</Label>
				</FormGroup>
				<FormGroup check>
					<Label check>
						<Input type="radio" name="gender" /> Femenino
					</Label>
				</FormGroup>
				<FormGroup>
					<Label for="exampleSelect">Nivel de confianza</Label>
					<Input type="select" name="select" id="exampleSelect">
						<option>1</option>
						<option>2</option>
						<option>3</option>
						<option>4</option>
						<option>5</option>
					</Input>
				</FormGroup>
				<FormGroup className="mt-3">
					<FormGroup>
						<Label for="exampleFile">Foto del rostro</Label>
						<Input type="file" name="file" id="exampleFile" className="" />
						<FormText color="muted">Formatos permitidos: .jpg y .png</FormText>
					</FormGroup>
				</FormGroup>
			</AvForm>
		</React.Fragment>
	);
};

export default AddUser;
