import React, {useReducer} from "react";

import faceContext from "./faceContext";
import faceReducer from "./faceReducer";
import {
	OBTENER_FACES,
	AGREGAR_FACE,
	AGREGAR_FOTO,
	FACE_ACTUAL,
	OBTENER_FACE_FILTRADO,
	OBTENER_FACE,
	ELIMINAR_FACE,
	ACTUALIZAR_FACE,
} from "../../types";

import clienteAxios from "../../config/axios";

const FaceState = (props) => {
	const initialState = {
		faces: [],
		face: null,
		faces_filtrados: [],
	};

	// Dispatch para ejecutar las acciones
	const [state, dispatch] = useReducer(faceReducer, initialState);

	// Obtener los faces
	const obtenerFaces = async () => {
		try {
			const resultado = await clienteAxios.get("/api/faces");
			obtenerImagenFace(resultado.data.image);
			dispatch({
				type: OBTENER_FACES,
				payload: resultado.data,
			});
		} catch (error) {
			console.log(error);
		}
	};

	// Obtener imagen del rostro
	const obtenerImagenFace = async (id) => {
		try {
			console.log(id);
		} catch (error) {
			console.log(error);
		}
	};

	// Agregar nuevo faces
	const agregarFace = async (face) => {
		try {
			const resultado = await clienteAxios.post("/api/save-face", face);
			console.log(resultado);
			//agregarFoto(resultado.data);
			// Insertar el faces en el state
			dispatch({
				type: AGREGAR_FACE,
				payload: resultado.data,
			});
		} catch (error) {
			console.log(error);
		}
	};

	// Agregar nuevo faces
	const agregarFoto = async (face) => {
		console.log(face);
		try {
			const resultado = await clienteAxios.post(
				`/api/upload-image-face/${face.id}`,
				face.image
			);
			console.log(resultado);
			// Insertar el faces en el state
			dispatch({
				type: AGREGAR_FOTO,
				payload: resultado.data,
			});
		} catch (error) {
			console.log(error);
		}
	};

	// Selecciona el Proyecto que el usuario dio click
	const faceActual = async (faceId) => {
		try {
			const resultado = await clienteAxios.get(`/api/face/${faceId}`);
			console.log(resultado);
			dispatch({
				type: OBTENER_FACE,
				payload: resultado.data,
			});
		} catch (error) {
			console.log(error);
		}
	};

	// Selecciona el Proyecto que el usuario dio click
	const faceByConfidenceLevel = async (faceId) => {
		try {
			console.log(`Face Id: ${faceId}`);
			const resultado = await clienteAxios.get(
				`/api/face-by-confidence-level/${faceId}`
			);
			console.log(resultado);
			dispatch({
				type: OBTENER_FACE_FILTRADO,
				payload: resultado.data,
			});
		} catch (error) {
			console.log(error);
		}
	};

	const actualizarFace = async (face) => {
		try {
			const resultado = await clienteAxios.put(
				`/api/update-face/${face._id}`,
				face
			);
			console.log(resultado);
			dispatch({
				type: ACTUALIZAR_FACE,
				payload: resultado.data.face,
			});
		} catch (error) {
			console.log(error);
		}
	};

	// Elimina un faces
	const eliminarFace = async (faceId) => {
		try {
			await clienteAxios.delete(`/api/face/${faceId}`);
			dispatch({
				type: ELIMINAR_FACE,
				payload: faceId,
			});
		} catch (error) {
			console.log(error);
		}
	};

	return (
		<faceContext.Provider
			value={{
				faces: state.faces,
				faces_filtrados: state.faces_filtrados,
				obtenerFaces,
				agregarFace,
				faceByConfidenceLevel,
				agregarFoto,
				faceActual,
				actualizarFace,
				eliminarFace,
			}}>
			{props.children}
		</faceContext.Provider>
	);
};

export default FaceState;
