import {
	FORMULARIO_FACE,
	OBTENER_FACES,
	AGREGAR_FACE,
	VALIDAR_FORMULARIO,
	FACE_ACTUAL,
	ELIMINAR_FACE,
} from "../../types";

export default (state, action) => {
	switch (action.type) {
		case FORMULARIO_FACE:
			return {
				...state,
				formulario: true,
			};
		case OBTENER_FACES:
			return {
				...state,
				faces: action.payload,
			};
		case AGREGAR_FACE:
			return {
				...state,
				faces: [...state.faces, action.payload],
				formulario: false,
				errorformulario: false,
			};
		case VALIDAR_FORMULARIO:
			return {
				...state,
				errorformulario: true,
			};
		case FACE_ACTUAL:
			return {
				...state,
				face: state.faces.filter((face) => face.id === action.payload),
			};
		case ELIMINAR_FACE:
			return {
				...state,
				faces: state.faces.filter((face) => face.id !== action.payload),
				face: null,
			};
		default:
			return state;
	}
};
