import React, {useContext, useEffect, useState} from "react";
import {
	FormGroup,
	Label,
	Input,
	FormText,
	Row,
	Col,
	Button,
	ModalHeader,
	ModalBody,
	ModalFooter,
} from "reactstrap";
import {
	AvForm,
	AvField,
	AvInput,
	AvRadioGroup,
	AvRadio,
} from "availity-reactstrap-validation";
import faceContext from "../../context/face/faceContext";

const AddUser = ({toggle}) => {
	const facesContext = useContext(faceContext);
	const {agregarProyecto} = facesContext;

	/*Creacion del objeto usuario */
	const [usuario, guardarUsuario] = useState({
		name: "",
		surname: "",
		age: "",
		sex: "",

		email: "",
		password: "",
	});

	const onSubmitProyecto = (e) => {
		e.preventDefault();
		console.log("enviado");
	};

	return (
		<React.Fragment>
			<ModalHeader toggle={toggle}>Añadir usuarios</ModalHeader>
			<ModalBody>
				<AvForm className="needs-validation mt-3" onSubmit={onSubmitProyecto}>
					<Row>
						<Col md="6">
							<AvField
								name="Nombre"
								label="Nombre"
								errorMessage="Introduce un nombre válido"
								className="form-control"
								validate={{required: {value: true}}}
							/>
						</Col>
						<Col md="6">
							<AvField
								name="surname"
								label="Apellido"
								errorMessage="Introduce un apellido válido"
								className="form-control"
								validate={{required: {value: true}}}
							/>
						</Col>
					</Row>

					<FormGroup>
						<Button className="btn-primary-flow ">Submit</Button>
					</FormGroup>
				</AvForm>
			</ModalBody>
		</React.Fragment>
	);
};

export default AddUser;
