import React, {useState, useEffect, useRef} from "react";
import * as faceapi from "face-api.js";
import {
	Container,
	Row,
	Col,
	Card,
	CardBody,
	ButtonGroup,
	Button,
	Modal,
	ModalHeader,
	ModalBody,
	ModalFooter,
} from "reactstrap";
//Import Breadcrumb
import Breadcrumbs from "../../components/Common/Breadcrumb";
import Webcam from "react-webcam";

const Camera = () => {
	const webcamRef = React.useRef(null);
	const canvasRef = useRef();
	const mediaRecorderRef = React.useRef(null);
	const [capturing, setCapturing] = React.useState(false);
	const [recordedChunks, setRecordedChunks] = React.useState([]);

	const handleStartCaptureClick = React.useCallback(() => {
		setCapturing(true);
		mediaRecorderRef.current = new MediaRecorder(webcamRef.current.stream, {
			mimeType: "video/webm",
		});
		mediaRecorderRef.current.addEventListener(
			"dataavailable",
			handleDataAvailable
		);
		mediaRecorderRef.current.start();
	}, [webcamRef, setCapturing, mediaRecorderRef]);

	const handleDataAvailable = React.useCallback(
		({data}) => {
			if (data.size > 0) {
				setRecordedChunks((prev) => prev.concat(data));
			}
		},
		[setRecordedChunks]
	);

	const handleStopCaptureClick = React.useCallback(() => {
		mediaRecorderRef.current.stop();
		setCapturing(false);
	}, [mediaRecorderRef, webcamRef, setCapturing]);

	const handleDownload = React.useCallback(() => {
		if (recordedChunks.length) {
			const blob = new Blob(recordedChunks, {
				type: "video/mp4",
			});
			const url = URL.createObjectURL(blob);
			const a = document.createElement("a");
			document.body.appendChild(a);
			a.style = "display: none";
			a.href = url;
			a.download = "react-webcam-stream-capture.mp4";
			a.click();
			window.URL.revokeObjectURL(url);
			setRecordedChunks([]);
		}
	}, [recordedChunks]);

	const handleDownloadImage = React.useCallback(() => {
		toggle();
		const blob = new Blob(recordedChunks, {
			type: "image/jpg",
		});
		const url = URL.createObjectURL(blob);
		const a = document.createElement("a");
		document.body.appendChild(a);
		a.style = "display: none";
		a.href = url;
		a.download = "react-webcam-stream-capture.jpg";
		a.click();
		window.URL.revokeObjectURL(url);
		setRecordedChunks([]);
	});

	const videoConstraints = {
		width: 1280,
		height: 720,
		facingMode: "user",
	};

	const [imgSrc, setImgSrc] = React.useState(null);

	const capture = React.useCallback(() => {
		toggle();
		const imageSrc = webcamRef.current.getScreenshot();
		setImgSrc(imageSrc);
	}, [webcamRef, setImgSrc]);

	const [modal, setModal] = useState(false);

	const toggle = () => setModal(!modal);

	const startVideo = () => {
		navigator.getUserMedia(
			{
				video: {},
			},
			(stream) => (webcamRef.current.srcObject = stream),
			(err) => console.log(err)
		);
	};

	return (
		<React.Fragment>
			<Modal isOpen={modal} toggle={toggle} centered={true}>
				<ModalHeader toggle={toggle}>Imagen capturada</ModalHeader>
				<ModalBody>
					{imgSrc && <img src={imgSrc} className="w-100" />}
				</ModalBody>
				<ModalFooter>
					<Button color="primary" onClick={handleDownloadImage}>
						Descargar
					</Button>{" "}
					<Button color="secondary" onClick={toggle}>
						Guardar
					</Button>
				</ModalFooter>
			</Modal>
			<div className="page-content">
				<Container fluid>
					<Breadcrumbs title="Cámaras" />
					<Row>
						<Col xl="12">
							<Card>
								<CardBody>
									<div className="float-right d-none d-md-inline-block">
										<ButtonGroup className="mb-4">
											{capturing ? (
												<Button
													size="md"
													color="light"
													type="button"
													onClick={handleStopCaptureClick}>
													Detener video
												</Button>
											) : (
												<Button
													size="md"
													color="light"
													type="button"
													onClick={handleStartCaptureClick}>
													Iniciar video
												</Button>
											)}
											{recordedChunks.length > 0 && (
												<Button
													onClick={handleDownload}
													size="md"
													color="success"
													type="button">
													Descargar
												</Button>
											)}

											<Button
												size="md"
												color="light"
												type="button"
												onClick={capture}>
												Capturar foto
											</Button>
										</ButtonGroup>
									</div>
									<h4 className="card-title mb-4">Sala de mi casa</h4>
									<div className="embed-responsive embed-responsive-21by9">
										<Webcam
											ref={webcamRef}
											width={1280}
											videoConstraints={videoConstraints}
											screenshotFormat="image/jpeg"
										/>
										<canvas className="position-absolute" ref={canvasRef} />
									</div>
								</CardBody>
							</Card>
						</Col>
					</Row>
				</Container>
			</div>
		</React.Fragment>
	);
};

export default Camera;
