import React, {useContext, useEffect, useState} from "react";
import {
	FormGroup,
	Label,
	Input,
	FormText,
	Row,
	Col,
	Button,
	ModalHeader,
	ModalBody,
	ModalFooter,
	Modal,
} from "reactstrap";
import {
	AvForm,
	AvField,
	AvInput,
	AvSelect,
	AvRadioGroup,
	AvRadio,
} from "availity-reactstrap-validation";
import faceContext from "../../context/face/faceContext";

const AddUser = ({userId}) => {
	const facesContext = useContext(faceContext);
	const {agregarFace, faces} = facesContext;

	const [modal, setModal] = useState(false);
	const toggle = () => setModal(!modal);

	/*Creacion del objeto usuario */
	const [usuario, guardarUsuario] = useState({
		name: "",
		surname: "",
		age: "",
		gender: "",
		confidenceLevels: "",
		image: "",
	});

	// Lee los contenidos del input
	const onChange = (e) => {
		guardarUsuario({
			...usuario,
			[e.target.name]: e.target.value,
		});
	};

	const onSubmitProyecto = (e) => {
		agregarFace(usuario);
		//Reiniciar formulario
		guardarUsuario({
			name: "",
			surname: "",
			age: "",
			gender: "",
			confidenceLevels: "",
			image: "",
		});
		toggle();
	};

	return (
		<React.Fragment>
			<Button
				color="dark"
				outline
				className=" waves-effect waves-light float-right mb-3"
				onClick={toggle}>
				Añadir usuario
			</Button>
			<Modal isOpen={modal} centered={true} toggle={toggle}>
				<ModalHeader toggle={toggle}>Añadir usuarios</ModalHeader>
				<ModalBody>
					<AvForm
						className="needs-validation mt-3"
						onValidSubmit={onSubmitProyecto}>
						<Row>
							<Col md="6">
								<AvField
									name="name"
									label="Nombre"
									errorMessage="Introduce un nombre válido"
									className="form-control"
									onChange={onChange}
									validate={{required: {value: true}}}
								/>
							</Col>
							<Col md="6">
								<AvField
									name="surname"
									label="Apellido"
									errorMessage="Introduce un apellido válido"
									onChange={onChange}
									className="form-control"
									validate={{required: {value: true}}}
								/>
							</Col>
						</Row>

						<AvField
							name="age"
							label="Edad"
							type="text"
							validate={{number: true}}
							errorMessage="Introduce una edad válida"
							className="form-control"
							onChange={onChange}
							required
						/>
						<Label>Sexo</Label>
						<AvRadioGroup
							inline
							name="gender"
							required
							errorMessage="Selecciona el sexo">
							<AvRadio
								label="Masculino"
								value="Masculino"
								onChange={onChange}
							/>
							<AvRadio label="Femenino" value="Femenino" onChange={onChange} />
							<AvRadio label="Otro" value="Otro" onChange={onChange} />
						</AvRadioGroup>

						<Label for="exampleSelect">Nivel de confianza</Label>

						<AvField
							type="select"
							required
							onChange={onChange}
							name="confidenceLevels"
							errorMessage="Selecciona el nivel de confianza al que pertenece el usuario">
							<option value="">Selecciona un nivel de confianza</option>
							<option value="60316459aea69a7200c32a64">1</option>
							<option value="603165309fc13d17bcbd7089">2</option>
							<option value="603167ef9fc13d17bcbd708a">3</option>
							<option value="603168839fc13d17bcbd708b">4</option>
						</AvField>
						<AvField
							type="file"
							required
							id="image"
							name="image"
							errorMessage="La foto del rostro del usuario es obligatoria"
							onChange={onChange}
						/>

						<FormGroup>
							<Button
								color="primary"
								className=" waves-effect waves-light mr-2">
								Añadir
							</Button>
						</FormGroup>
					</AvForm>
				</ModalBody>
			</Modal>
		</React.Fragment>
	);
};

export default AddUser;
