import React, {useContext, useEffect, useState} from "react";
import {
	Row,
	Col,
	CardBody,
	Modal,
	CardTitle,
	CardImg,
	Button,
	CardText,
	ModalHeader,
	ModalBody,
	ModalFooter,
} from "reactstrap";
//Import Images
import img2 from "../../assets/images/small/img-2.jpg";
import faceContext from "../../context/face/faceContext";

const GetUsers = ({face, eliminarUsuario}) => {
	const [modal, setModal] = useState(false);
	const toggle = () => setModal(!modal);

	const facesContext = useContext(faceContext);
	const {actualizarFace} = facesContext;

	/*Creacion del objeto usuario */
	const [usuario, actualizarUsuario] = useState({
		name: "",
		surname: "",
		age: "",
		gender: "",
		confidenceLevels: "",
		image: "",
	});

	// Lee los contenidos del input
	const onChange = (e) => {
		actualizarUsuario({
			...usuario,
			[e.target.name]: e.target.value,
		});
	};

	const onSubmitUsuario = (e) => {
		actualizarFace(usuario);
		//Reiniciar formulario
		actualizarUsuario({
			name: "",
			surname: "",
			age: "",
			gender: "",
			confidenceLevels: "",
			image: "",
		});
		toggle();
	};

	return (
		<Row className="no-gutters align-items-center">
			<Col md={3}>
				<CardImg className="img-fluid" src={img2} alt="Skote" />
			</Col>
			<Col md={9}>
				<CardBody>
					<CardTitle>
						<h5>
							{face.name} {face.surname}
						</h5>
					</CardTitle>
					<CardText>
						<strong>Edad:</strong> {face.age} - <strong>Sexo:</strong>{" "}
						{face.gender}
					</CardText>
					<CardText>
						<Button
							color="primary"
							className=" waves-effect waves-light mr-2"
							onClick={toggle}>
							Editar
						</Button>
						<Button
							color="secondary"
							outline
							className=" waves-effect waves-light"
							onClick={() => eliminarUsuario(face._id)}>
							Eliminar
						</Button>
					</CardText>
				</CardBody>
			</Col>
			<Modal isOpen={modal} centered={true} toggle={toggle}>
				<ModalHeader toggle={toggle}>Editar usuario</ModalHeader>
				<ModalBody>
					<h1>{face._id}</h1>
				</ModalBody>
			</Modal>
		</Row>
	);
};

export default GetUsers;
