import React, {useContext, useEffect, useState} from "react";
import {
	Row,
	Col,
	CardBody,
	Modal,
	CardTitle,
	CardImg,
	Button,
	CardText,
	ModalHeader,
	ModalBody,
	FormGroup,
	ModalFooter,
} from "reactstrap";
import {
	AvForm,
	AvField,
	AvInput,
	AvSelect,
	AvRadioGroup,
	AvRadio,
} from "availity-reactstrap-validation";
//Import Images
import img2 from "../../assets/images/small/img-2.jpg";
import faceContext from "../../context/face/faceContext";

const GetUsers = ({id}) => {
	const [modal, setModal] = useState(false);
	const toggle = () => setModal(!modal);

	const facesContext = useContext(faceContext);
	const {faceByConfidenceLevel, faces_filtrados} = facesContext;

	const [list, setList] = useState([]);
	useEffect(() => {
		let mounted = true;
		faceByConfidenceLevel(id).then((items) => {
			if (mounted) {
				setList(items);
			}
		});
		return () => (mounted = false);
	}, []);

	/*Creacion del objeto usuario */
	const [usuario, actualizarUsuario] = useState({
		_id: id,
		name: "",
		surname: "",
		age: "",
		gender: "",
		confidenceLevels: "",
		image: "",
	});

	// Lee los contenidos del input
	const onChange = (e) => {
		actualizarUsuario({
			...usuario,
			[e.target.name]: e.target.value,
		});
	};

	const onSubmitUsuario = (e) => {
		//actualizarFace(usuario);
		toggle();
	};

	return (
		<div className="wrapper">
			<h1>My Grocery List</h1>
			<ul>
				{list.map((item) => (
					<li key={item.item}>{item.item}</li>
				))}
			</ul>
		</div>
	);
};

export default GetUsers;
