import React, {useContext, useEffect, useState} from "react";
import {
	FormGroup,
	Label,
	Input,
	FormText,
	Row,
	Col,
	Button,
	ModalHeader,
	ModalBody,
	ModalFooter,
} from "reactstrap";
import {
	AvForm,
	AvField,
	AvInput,
	AvRadioGroup,
	AvRadio,
} from "availity-reactstrap-validation";
import faceContext from "../../context/face/faceContext";

const AddUser = ({toggle}) => {
	const facesContext = useContext(faceContext);
	const {agregarFace} = facesContext;

	/*Creacion del objeto usuario */
	const [usuario, guardarUsuario] = useState({
		name: "",
		surname: "",
		age: "",
		gender: "",
		confidenceLevels: "",
	});

	// Lee los contenidos del input
	const onChange = (e) => {
		guardarUsuario({
			...usuario,
			[e.target.name]: e.target.value,
		});
	};

	const onSubmitProyecto = (e) => {
		console.log("enviado");
		// agregar al state
		agregarFace(usuario);
	};

	return (
		<React.Fragment>
			<ModalHeader toggle={toggle}>Añadir usuarios</ModalHeader>
			<ModalBody>
				<AvForm className="needs-validation mt-3" onSubmit={onSubmitProyecto}>
					<Row>
						<Col md="6">
							<AvField
								name="name"
								label="Nombre"
								errorMessage="Introduce un nombre válido"
								className="form-control"
								onChange={onChange}
								validate={{required: {value: true}}}
							/>
						</Col>
						<Col md="6">
							<AvField
								name="surname"
								label="Apellido"
								errorMessage="Introduce un apellido válido"
								onChange={onChange}
								className="form-control"
								validate={{required: {value: true}}}
							/>
						</Col>
					</Row>

					<AvField
						name="age"
						label="Edad"
						type="text"
						validate={{number: true}}
						errorMessage="Introduce una edad válida"
						className="form-control"
						onChange={onChange}
						required
					/>
					<Label>Sexo</Label>
					<AvRadioGroup inline name="gender" required>
						<AvRadio label="Masculino" value="Masculino" onChange={onChange} />
						<AvRadio label="Femenino" value="Femenino" onChange={onChange} />
						<AvRadio label="Otro" value="Otro" onChange={onChange} />
					</AvRadioGroup>

					<Label for="exampleSelect">Nivel de confianza</Label>
					<AvField
						type="select"
						onChange={onChange}
						name="confidenceLevels"
						helpMessage="Escoge el nivel de confianza al que pertenece el usuario">
						<option>1</option>
						<option>2</option>
						<option>3</option>
						<option>4</option>
						<option>5</option>
					</AvField>
					<FormGroup className="mt-3">
						<Label for="exampleFile">Foto del rostro</Label>
						<Input
							type="file"
							name="file"
							id="exampleFile"
							className=""
							onChange={onChange}
						/>
						<FormText color="muted">Formatos permitidos: .jpg y .png</FormText>
					</FormGroup>
					<FormGroup>
						<Button color="primary" className=" waves-effect waves-light mr-2">
							Submit
						</Button>
					</FormGroup>
				</AvForm>
			</ModalBody>
		</React.Fragment>
	);
};

export default AddUser;
