import React, {useReducer, useState} from "react";
import AuthContext from "./authContext";
import AuthReducer from "./authReducer";

import clienteAxios from "../../config/axios";
import tokenAuth from "../../config/token";

import {
	REGISTRO_EXITOSO,
	ACTUALIZAR_USUARIO,
	OBTENER_USUARIO,
	LOGIN_EXITOSO,
	LOGIN_ERROR,
	CERRAR_SESION,
	OBTENER_DATOS_USUARIO,
} from "../../types";

const AuthState = (props) => {
	const initialState = {
		token: localStorage.getItem("token"),
		autenticado: null,
		usuario: null,
		mensaje: null,
		cargando: true,
	};

	const [id, setId] = useState("");

	const [state, dispatch] = useReducer(AuthReducer, initialState);

	const registrarUsuario = async (body) => {
		console.log(body);
		try {
			const respuesta = await clienteAxios.post("/api/register", body);

			dispatch({
				type: REGISTRO_EXITOSO,
				payload: respuesta.data.token,
			});
			setId(respuesta.data.id);
			usuarioAutenticado();
		} catch (error) {
			// console.log(error.response.data.msg);
		}
	};

	// Retorna el usuario autenticado
	const usuarioAutenticado = async () => {
		const token = localStorage.getItem("token");

		if (token) {
			tokenAuth(token);
		}

		try {
			const respuesta = await clienteAxios.get("/api/auth-user");
			dispatch({
				type: OBTENER_USUARIO,
				payload: respuesta.data.user,
			});
		} catch (error) {
			dispatch({
				type: LOGIN_ERROR,
			});
		}
	};

	const obtenerUsuario = async () => {
		try {
			const respuesta = await clienteAxios.get(`/api/get-user ${id}`);
			console.log(respuesta);
			dispatch({
				type: OBTENER_DATOS_USUARIO,
				payload: respuesta.data,
			});
		} catch (error) {
			console.log(error);
		}
	};

	const actualizarUsuario = async (body) => {
		console.log(body);
		try {
			/*
			const respuesta = await clienteAxios.post(`/api/update-user/`, body);
			dispatch({
				type: ACTUALIZAR_USUARIO,
				payload: respuesta.data.token,
			});
			*/
		} catch (error) {
			// console.log(error.response.data.msg);
		}
	};

	// Cuando el usuario inicia sesión
	const iniciarSesion = async (datos) => {
		let payload = datos;
		payload.gethash = true;
		try {
			const respuesta = await clienteAxios.post("/api/login", payload);
			setId(respuesta.data.id);
			dispatch({
				type: LOGIN_EXITOSO,
				payload: respuesta.data.token,
			});
			usuarioAutenticado();
		} catch (error) {}
	};

	//Cerrar sesion del usuario
	// Cierra la sesión del usuario
	const cerrarSesion = () => {
		dispatch({
			type: CERRAR_SESION,
		});
	};

	return (
		<AuthContext.Provider
			value={{
				token: state.token,
				autenticado: state.autenticado,
				usuario: state.usuario,
				mensaje: state.mensaje,
				cargando: state.cargando,
				registrarUsuario,
				iniciarSesion,
				usuarioAutenticado,
				cerrarSesion,
				actualizarUsuario,
				obtenerUsuario,
			}}>
			{props.children}
		</AuthContext.Provider>
	);
};
export default AuthState;
