import React, {useContext, useState, useEffect} from "react";
import {Row, Col, Card, CardBody, Container, Modal, Button} from "reactstrap";
//Import Breadcrumb
import Breadcrumbs from "../../components/Common/Breadcrumb";
import AddUser from "./AddUser";
import Nivel from "./Nivel";
import GetUsers from "./GetUsers";
import faceContext from "../../context/face/faceContext";

const ConfidenceLevels = (props) => {
	const {className} = props;
	const [modal, setModal] = useState(false);
	const toggle = () => setModal(!modal);

	const facesContext = useContext(faceContext);
	const {obtenerFaces, faces} = facesContext;

	const [list, setList] = useState([]);

	useEffect(() => {
		let mounted = true;
		obtenerFaces().then((items) => {
			if (mounted) {
				setList(items);
			}
		});
		return () => (mounted = false);
	}, []);

	let filterFacesLevel1 = faces.filter(
		(face) => face.confidenceLevels === "60316459aea69a7200c32a64"
	);

	let filterFacesLevel2 = faces.filter(
		(face) => face.confidenceLevels === "603165309fc13d17bcbd7089"
	);

	let filterFacesLevel3 = faces.filter(
		(face) => face.confidenceLevels === "603167ef9fc13d17bcbd708a"
	);

	let filterFacesLevel4 = faces.filter(
		(face) => face.confidenceLevels === "603168839fc13d17bcbd708b"
	);

	return (
		<React.Fragment>
			<div className="page-content">
				<Container fluid>
					{/*Header*/}
					<Row>
						<Col md="6">
							<Breadcrumbs title="Niveles de confianza" />
						</Col>
						<Col md="6">
							<Button
								color="dark"
								outline
								className=" waves-effect waves-light float-right mb-3"
								onClick={toggle}>
								AÃ±adir usuario
							</Button>
						</Col>
					</Row>

					<Row>
						<Col xl="6">
							<Card>
								<CardBody>
									<Row>
										<Col md="9">
											<h4 className="card-title">Nivel 1</h4>
											<p>
												Personas de confianza extrema como familiares y mejores
												amigos
											</p>
										</Col>
									</Row>

									<div className="table-responsive mt-2 overflow-scroll height-confidencelevels">
										{filterFacesLevel1.map((item) => (
											<GetUsers key={item._id} item={item}></GetUsers>
										))}
									</div>
								</CardBody>
							</Card>
						</Col>
						<Col xl="6">
							<Card>
								<CardBody>
									<Row>
										<Col md="9">
											<h4 className="card-title">Nivel 2</h4>
											<p>
												Personas conocidas que frecuentan los alrededores del
												recinto como personal de aseo, vecinos, etc.
											</p>
										</Col>
									</Row>

									<div className="table-responsive mt-2 overflow-scroll height-confidencelevels">
										{filterFacesLevel2.map((item) => (
											<GetUsers key={item._id} item={item}></GetUsers>
										))}
									</div>
								</CardBody>
							</Card>
						</Col>
						<Col xl="6">
							<Card>
								<CardBody>
									<Row>
										<Col md="9">
											<h4 className="card-title">Nivel 3</h4>
											<p>
												Personas totalmente desconocidas pero no se consideran
												una amenaza.
											</p>
										</Col>
									</Row>

									<div className="table-responsive mt-2 overflow-scroll height-confidencelevels">
										{filterFacesLevel3.map((item) => (
											<GetUsers key={item._id} item={item}></GetUsers>
										))}
									</div>
								</CardBody>
							</Card>
						</Col>
						<Col xl="6">
							<Card>
								<CardBody>
									<Row>
										<Col md="9">
											<h4 className="card-title">Nivel 4</h4>
											<p>
												Personas que son consideradas una amenaza y no son
												permitidas en el recinto.
											</p>
										</Col>
									</Row>

									<div className="table-responsive mt-2 overflow-scroll height-confidencelevels">
										{filterFacesLevel4.map((item) => (
											<GetUsers key={item._id} item={item}></GetUsers>
										))}
									</div>
								</CardBody>
							</Card>
						</Col>
					</Row>
				</Container>
			</div>
			<Modal
				isOpen={modal}
				centered={true}
				toggle={toggle}
				className={className}>
				<AddUser toggle={toggle}></AddUser>
			</Modal>
		</React.Fragment>
	);
};

export default ConfidenceLevels;
