import React, {useReducer} from "react";

import faceContext from "./faceContext";
import faceReducer from "./faceReducer";
import {
	FORMULARIO_FACE,
	OBTENER_FACES,
	AGREGAR_FACE,
	VALIDAR_FORMULARIO,
	FACE_ACTUAL,
	ELIMINAR_FACE,
} from "../../types";

import clienteAxios from "../../config/axios";

const ProyectoState = (props) => {
	const faces = [
		{id: 1, nombre: "Tienda Virtual"},
		{id: 2, nombre: "Intranet"},
		{id: 3, nombre: "Diseño de Sitio web"},
		{id: 4, nombre: "MERN "},
	];

	const initialState = {
		faces: [],
		formulario: false,
		errorformulario: false,
		face: null,
	};

	// Dispatch para ejecutar las acciones
	const [state, dispatch] = useReducer(faceReducer, initialState);

	// Serie de funciones para el CRUD
	const mostrarFormulario = () => {
		dispatch({
			type: FORMULARIO_FACE,
		});
	};

	// Obtener los faces
	const obtenerFaces = () => {
		dispatch({
			type: OBTENER_FACES,
			payload: faces,
		});
	};

	// Agregar nuevo face
	const agregarFace = async (face) => {
		try {
			const resultado = await clienteAxios.post("/api/face", face);
			console.log(resultado);
			// Insertar el face en el state
			dispatch({
				type: AGREGAR_FACE,
				payload: resultado.data,
			});
		} catch (error) {
			console.log(error);
		}
	};

	// Valida el formulario por errores
	const mostrarError = () => {
		dispatch({
			type: VALIDAR_FORMULARIO,
		});
	};

	// Selecciona el Proyecto que el usuario dio click
	const faceActual = (faceId) => {
		dispatch({
			type: FACE_ACTUAL,
			payload: faceId,
		});
	};

	// Elimina un face
	const eliminarFace = (faceId) => {
		dispatch({
			type: ELIMINAR_FACE,
			payload: faceId,
		});
	};

	return (
		<faceContext.Provider
			value={{
				faces: state.faces,
				formulario: state.formulario,
				errorformulario: state.errorformulario,
				face: state.face,
				mostrarFormulario,
				obtenerFaces,
				agregarFace,
				mostrarError,
				faceActual,
				eliminarFace,
			}}>
			{props.children}
		</faceContext.Provider>
	);
};

export default ProyectoState;
