import {
	FORMULARIO_FACE,
	OBTENER_FACES,
	AGREGAR_FACE,
	VALIDAR_FORMULARIO,
	FACE_ACTUAL,
	ELIMINAR_FACE,
} from "../../types";

export default (state, action) => {
	switch (action.type) {
		case FORMULARIO_FACE:
			return {
				...state,
				formulario: true,
			};
		case OBTENER_FACES:
			return {
				...state,
				proyectos: action.payload,
			};
		case AGREGAR_FACE:
			return {
				...state,
				proyectos: [...state.proyectos, action.payload],
				formulario: false,
				errorformulario: false,
			};
		case VALIDAR_FORMULARIO:
			return {
				...state,
				errorformulario: true,
			};
		case FACE_ACTUAL:
			return {
				...state,
				proyecto: state.proyectos.filter(
					(proyecto) => proyecto.id === action.payload
				),
			};
		case ELIMINAR_FACE:
			return {
				...state,
				proyectos: state.proyectos.filter(
					(proyecto) => proyecto.id !== action.payload
				),
				proyecto: null,
			};
		default:
			return state;
	}
};
