import React, {useContext, useEffect, useState} from "react";
import {
	Row,
	Col,
	Card,
	CardBody,
	Container,
	Modal,
	Button,
	Label,
} from "reactstrap";
//Import Breadcrumb
import Breadcrumbs from "../../components/Common/Breadcrumb";
import AddUser from "./AddUser";
import GetUsers from "./GetUsers";
import faceContext from "../../context/face/faceContext";

const ConfidenceLevels = (props) => {
	const [modal, setModal] = useState(false);
	const toggle = () => setModal(!modal);

	const [zid, setZid] = useState(1);

	// Extrar proyectos de state inicial
	const facesContext = useContext(faceContext);
	const {
		faces,
		obtenerFaces,
		eliminarFace,
		faceByConfidenceLevel,
		faces_filtrados,
	} = facesContext;

	useEffect(() => {
		return () => {
			obtenerFaces();
			faceByConfidenceLevel(zid);
		};
	}, []);

	const niveles = [
		{
			_id: "60316459aea69a7200c32a64",
			title: "Nivel 1",
			description:
				"Personas de confianza extrema como familiares y mejores amigos",
		},
		{
			_id: "603165309fc13d17bcbd7089",
			title: "Nivel 2",
			description:
				"Personas conocidas que frecuentan los alrededores del recinto como personal de aseo, vecinos, etc.",
		},
		{
			_id: "603167ef9fc13d17bcbd708a",
			title: "Nivel 3",
			description:
				"Personas totalmente desconocidas pero no se consideran una amenaza.",
		},
		{
			_id: "603168839fc13d17bcbd708b",
			title: "Nivel 4",
			description:
				"Personas que son consideradas una amenaza y no son permitidas en el recinto.",
		},
	];

	// revisar si proyectos tiene contenido
	if (faces.length === 0) return <p>No hay usuarios, comienza creando uno</p>;

	const eliminarUsuario = (id) => {
		eliminarFace(id);
	};

	return (
		<React.Fragment>
			<div className="page-content">
				<Container fluid>
					<Row>
						<Col md="6">
							<Breadcrumbs title="Niveles de confianza" />
						</Col>
						<Col md="6">
							<Button
								color="dark"
								outline
								className=" waves-effect waves-light float-right mb-3"
								onClick={toggle}>
								Añadir usuario
							</Button>
						</Col>
					</Row>

					<Row>
						<Col xl="6">
							<Card>
								<CardBody>
									<Row>
										<Col md="12">
											<h4 className="card-title">Nivel 1</h4>
											<p>
												Personas totalmente desconocidas pero no son
												consideradas como una amenaza.
											</p>
										</Col>
									</Row>
									<div className="table-responsive mt-2 overflow-scroll height-confidencelevels">
										<GetUsers></GetUsers>
									</div>
								</CardBody>
							</Card>
						</Col>
					</Row>
				</Container>
			</div>
			<Modal isOpen={modal} centered={true} toggle={toggle}>
				<AddUser toggle={toggle}></AddUser>
			</Modal>
		</React.Fragment>
	);
};

export default ConfidenceLevels;
