import React, {useState} from "react";
import {
	Container,
	Row,
	Col,
	Card,
	CardBody,
	ButtonGroup,
	Button,
	Modal,
	ModalHeader,
	ModalBody,
	ModalFooter,
} from "reactstrap";
//Import Breadcrumb
import Breadcrumbs from "../../components/Common/Breadcrumb";
import Webcam from "react-webcam";

const Camera = () => {
	const webcamRef = React.useRef(null);
	const mediaRecorderRef = React.useRef(null);
	const [capturing, setCapturing] = React.useState(false);
	const [recordedChunks, setRecordedChunks] = React.useState([]);

	const videoConstraints = {
		width: 1280,
		height: 720,
		facingMode: "user",
	};

	const [imgSrc, setImgSrc] = React.useState(null);

	const capture = React.useCallback(() => {
		toggle();
		const imageSrc = webcamRef.current.getScreenshot();
		setImgSrc(imageSrc);
	}, [webcamRef, setImgSrc]);

	const [modal, setModal] = useState(false);

	const toggle = () => setModal(!modal);

	return (
		<React.Fragment>
			{/* 
			<Modal isOpen={modal} toggle={toggle} centered={true}>
				<ModalHeader toggle={toggle}>Imagen capturada</ModalHeader>
				<ModalBody>
					{imgSrc && <img src={imgSrc} className="w-100" />}
				</ModalBody>
				<ModalFooter>
					<Button color="primary" onClick={handleDownloadImage}>
						Descargar
					</Button>{" "}
					<Button color="secondary" onClick={toggle}>
						Guardar
					</Button>
				</ModalFooter>
			</Modal>
			*/}
			<div className="page-content">
				<Container fluid>
					<Breadcrumbs title="CÃ¡maras" />
					<Row>
						<Col xl="12">
							<Card>
								<CardBody>
									{/* 
									<div className="float-right d-none d-md-inline-block">
										<ButtonGroup className="mb-4">
											{capturing ? (
												<Button
													size="md"
													color="light"
													type="button"
													onClick={handleStopCaptureClick}>
													Detener video
												</Button>
											) : (
												<Button
													size="md"
													color="light"
													type="button"
													onClick={handleStartCaptureClick}>
													Iniciar video
												</Button>
											)}
											{recordedChunks.length > 0 && (
												<Button
													onClick={handleDownload}
													size="md"
													color="success"
													type="button">
													Descargar
												</Button>
											)}

											<Button
												size="md"
												color="light"
												type="button"
												onClick={capture}>
												Capturar foto
											</Button>
										</ButtonGroup>
									</div>
									*/}
									<h4 className="card-title mb-4">Sala de mi casa</h4>
									<div className="embed-responsive embed-responsive-21by9">
										<Webcam
											ref={webcamRef}
											width={1280}
											videoConstraints={videoConstraints}
											screenshotFormat="image/jpeg"
										/>
									</div>
								</CardBody>
							</Card>
						</Col>
					</Row>
				</Container>
			</div>
		</React.Fragment>
	);
};

export default Camera;
