import React, {useContext, useEffect, useState} from "react";
import {
	Row,
	Col,
	Card,
	CardBody,
	Container,
	Modal,
	ModalHeader,
	FormGroup,
	Label,
	Input,
	ModalBody,
	ModalFooter,
	Button,
} from "reactstrap";
//Import Breadcrumb
import Breadcrumbs from "../../components/Common/Breadcrumb";
import AddUser from "./AddUser";
import GetUsers from "./GetUsers";
import {AvForm, AvField, AvInput} from "availity-reactstrap-validation";
import faceContext from "../../context/face/faceContext";

const ConfidenceLevels = (props) => {
	const {className} = props;

	const [modal, setModal] = useState(false);

	const toggle = () => setModal(!modal);

	// Extrar proyectos de state inicial
	const facesContext = useContext(faceContext);
	const {faces, obtenerFaces, eliminarFace, actualizarFace} = facesContext;

	useEffect(() => {
		obtenerFaces();
	});

	// revisar si proyectos tiene contenido
	if (faces.length === 0) return <p>No hay proyectos, comienza creando uno</p>;

	const eliminarUsuario = (id) => {
		eliminarFace(id);
	};

	const actualizarUsuario = () => {
		actualizarFace();
	};

	return (
		<React.Fragment>
			<div className="page-content">
				<Container fluid>
					{/*Header*/}
					<Row>
						<Col md="6">
							<Breadcrumbs title="Niveles de confianza" />
						</Col>
						<Col md="6">
							<Button
								color="dark"
								outline
								className=" waves-effect waves-light float-right mb-3"
								onClick={toggle}>
								Añadir usuario
							</Button>
						</Col>
					</Row>

					<Row>
						<Col xl="6">
							<Card>
								<CardBody>
									<Row>
										<Col md="12">
											<h4 className="card-title">Nivel 1</h4>
											<p>
												Personas totalmente desconocidas pero no son
												consideradas como una amenaza.
											</p>
										</Col>
									</Row>
									<div className="table-responsive mt-2 overflow-scroll height-confidencelevels">
										{faces.map((face) => (
											<GetUsers
												key={face._id}
												face={face}
												eliminarUsuario={eliminarUsuario}></GetUsers>
										))}
									</div>
								</CardBody>
							</Card>
						</Col>
					</Row>
				</Container>
			</div>
			<Modal
				isOpen={modal}
				centered={true}
				toggle={toggle}
				className={className}>
				<AddUser toggle={toggle}></AddUser>
			</Modal>
		</React.Fragment>
	);
};

export default ConfidenceLevels;
