import React, {useContext, useEffect, useState} from "react";
import {
	FormGroup,
	Label,
	Input,
	FormText,
	Row,
	Col,
	Button,
	Modal,
	ModalHeader,
	ModalBody,
	ModalFooter,
} from "reactstrap";
import {
	AvForm,
	AvField,
	AvInput,
	AvSelect,
	AvRadioGroup,
	AvRadio,
} from "availity-reactstrap-validation";
import faceContext from "../../context/face/faceContext";

const UpdateUser = ({toggle}) => {
	const facesContext = useContext(faceContext);
	const {actualizarFace} = facesContext;

	/*Creacion del objeto usuario */
	const [usuario, actualizarUsuario] = useState({
		name: "",
		surname: "",
		age: "",
		gender: "",
		confidenceLevels: "",
		image: "",
	});

	// Lee los contenidos del input
	const onChange = (e) => {
		actualizarUsuario({
			...usuario,
			[e.target.name]: e.target.value,
		});
	};

	const onSubmitUsuario = (e) => {
		actualizarFace(usuario);
		//Reiniciar formulario
		actualizarUsuario({
			name: "",
			surname: "",
			age: "",
			gender: "",
			confidenceLevels: "",
			image: "",
		});
		toggle();
	};

	return (
		<React.Fragment>
			<Row>
				<Col xl="12">
					<Card>
						<CardBody>
							<h3 className="card-title">Editar tu datos personales</h3>
							<AvForm className="needs-validation mt-3">
								<Row>
									<Col md="4"></Col>
									<Col md="4"></Col>
									<Col md="4"></Col>
								</Row>
								<Button color="primary" type="submit">
									Actualizar datos
								</Button>
							</AvForm>
						</CardBody>
					</Card>
				</Col>
			</Row>
		</React.Fragment>
	);
};

export default UpdateUser;
