import React, { Component, useContext,useEffect, useState } from "react";
import { Row, Col, Button, Alert, Container, Form, FormGroup, Label, Input} from "reactstrap";

import { Link } from "react-router-dom";

// import images
import logodark from "../../assets/images/logo-dark.png";

import AuthContext from "../../context/autenticacion/authContext";


const Register = (props) => {
    	/*Creacion del objeto usuario */
	const [usuario, guardarUsuario] = useState({
		name: "",
		surname: "",
		email: "",
		password: "",
	});

	const authContext = useContext(AuthContext);
	const {mensaje, autenticado, registrarUsuario} = authContext;

	// En caso de que el usuario se haya autenticado o registrado o sea un registro duplicado
	useEffect(() => {
		if (autenticado) {
			props.history.push("/listado-camaras");
		}

		if (mensaje) {
			console.log(mensaje.message);
		}
	}, [mensaje, autenticado, props.history]);

	const {name, surname, email, password} = usuario;

	const actualizarState = (e) => {
		guardarUsuario({
			...usuario,
			[e.target.name]: e.target.value,
		});
	};

	/*Validacion de inputs*/
	const [inputFirstname, setFirstname] = useState(false);
	const [inputLastname, setLastname] = useState(false);
	const [inputEmail, setEmail] = useState(false);
	const [inputPassword, setPassword] = useState(false);

	const onSubmit = (e) => {
		e.preventDefault();
		registrarUsuario({name, surname, email, password});
	};
    return (
        <React.Fragment>
        <div className="home-btn d-none d-sm-block">
            <Link to="/"><i className="mdi mdi-home-variant h2 text-white"></i></Link>
        </div>
        <div>
            <Container fluid className="p-0">
                <Row className="no-gutters">
                    <Col lg={4}>
                        <div className="authentication-page-content p-4 d-flex align-items-center min-vh-100">
                            <div className="w-100">
                                <Row className="justify-content-center">
                                    <Col lg={9}>
                                        <div>
                                            <div className="text-center">
                                                <div>
                                                    <Link to="#" className="logo"><img src={logodark} height="20" alt="logo"/></Link>
                                                </div>
    
                                                <h4 className="font-size-18 mt-4">Register account</h4>
                                                <p className="text-muted">Get your free Nazox account now.</p>
                                            </div>

                                    

                                            <div className="p-2 mt-5">
                                            <Form>
                                                <FormGroup>
                                                    <Label for="exampleEmail">Email</Label>
                                                    <Input type="email" name="email" id="exampleEmail" placeholder="with a placeholder" />
                                                </FormGroup>
                                                <FormGroup>
        <Label for="examplePassword">Password</Label>
        <Input type="password" name="password" id="examplePassword" placeholder="password placeholder" />
      </FormGroup>
                                            </Form>
                                            </div>

                                            <div className="mt-5 text-center">
                                                <p>Already have an account ? <Link to="/login" className="font-weight-medium text-primary"> Login</Link> </p>
                                                <p>Â© 2020 Nazox. Crafted with <i className="mdi mdi-heart text-danger"></i> by Themesdesign</p>
                                            </div>
                                        </div>

                                    </Col>
                                </Row>
                            </div>
                        </div>
                    </Col>
                    <Col lg={8}>
                        <div className="authentication-bg">
                            <div className="bg-overlay"></div>
                        </div>
                    </Col>
                </Row>
            </Container>
        </div>

      </React.Fragment>
    );
};

export default Register;
