import React, {useContext, useEffect, useState} from "react";
import {
	Row,
	Col,
	Card,
	CardBody,
	Container,
	Modal,
	Button,
	Label,
} from "reactstrap";
//Import Breadcrumb
import Breadcrumbs from "../../components/Common/Breadcrumb";
import AddUser from "./AddUser";
import GetUsers from "./GetUsers";
import UpdateUser from "./UpdateUser";
import faceContext from "../../context/face/faceContext";

const ConfidenceLevels = (props) => {
	const {className} = props;

	const [modal, setModal] = useState(false);
	const toggle = () => setModal(!modal);

	const [modal_xlarge, setModalxLarge] = useState(false);
	const tog_xlarge = () => setModalxLarge(!modal_xlarge);

	// Extrar proyectos de state inicial
	const facesContext = useContext(faceContext);
	const {faces, obtenerFaces, eliminarFace} = facesContext;

	useEffect(() => {
		obtenerFaces();
	});

	// revisar si proyectos tiene contenido
	if (faces.length === 0) return <p>No hay proyectos, comienza creando uno</p>;

	const eliminarUsuario = (id) => {
		eliminarFace(id);
	};

	return (
		<React.Fragment>
			<div className="page-content">
				<Container fluid>
					<Row>
						<Col md="6">
							<Breadcrumbs title="Niveles de confianza" />
						</Col>
						<Col md="6">
							<Button
								color="dark"
								outline
								className=" waves-effect waves-light float-right mb-3"
								onClick={toggle}>
								AÃ±adir usuario
							</Button>
						</Col>
					</Row>

					<Row>
						<Col xl="6">
							<Card>
								<CardBody>
									<Row>
										<Col md="12">
											<h4 className="card-title">Nivel 1</h4>
											<p>
												Personas totalmente desconocidas pero no son
												consideradas como una amenaza.
											</p>
										</Col>
									</Row>
									<div className="table-responsive mt-2 overflow-scroll height-confidencelevels">
										{faces.map((face) => (
											<GetUsers
												key={face._id}
												face={face}
												eliminarUsuario={eliminarUsuario}></GetUsers>
										))}
									</div>
								</CardBody>
							</Card>
						</Col>
					</Row>
				</Container>
			</div>
			<Modal isOpen={modal} centered={true} toggle={toggle}>
				<AddUser toggle={toggle}></AddUser>
			</Modal>

			<Modal isOpen={modal_xlarge} centered={true} toggle={tog_xlarge}>
				<UpdateUser tog_xlarge={tog_xlarge}></UpdateUser>
			</Modal>
		</React.Fragment>
	);
};

export default ConfidenceLevels;
