import React, {useReducer} from "react";

import faceContext from "./faceContext";
import faceReducer from "./faceReducer";
import {
	OBTENER_FACES,
	AGREGAR_FACE,
	AGREGAR_FOTO,
	FACE_ACTUAL,
	ELIMINAR_FACE,
} from "../../types";

import clienteAxios from "../../config/axios";

const FaceState = (props) => {
	const faces = [
		{id: 1, nombre: "Tienda Virtual"},
		{id: 2, nombre: "Intranet"},
		{id: 3, nombre: "Diseño de Sitio web"},
		{id: 4, nombre: "MERN "},
	];

	const initialState = {
		faces: [],
		face: null,
	};

	// Dispatch para ejecutar las acciones
	const [state, dispatch] = useReducer(faceReducer, initialState);

	// Obtener los proyectos
	const obtenerFaces = async () => {
		try {
			const resultado = await clienteAxios.get("/api/faces");
			console.log(resultado);

			dispatch({
				type: OBTENER_FACES,
				payload: resultado.data,
			});
		} catch (error) {
			console.log(error);
		}
	};

	// Agregar nuevo proyecto
	const agregarFace = async (face) => {
		console.log(face);
		try {
			const resultado = await clienteAxios.post("/api/face", face);
			console.log(resultado);
			// Insertar el proyecto en el state
			dispatch({
				type: AGREGAR_FACE,
				payload: resultado.data,
			});
		} catch (error) {
			console.log(error);
		}
	};

	// Agregar nuevo proyecto
	const agregarFoto = async (face) => {
		console.log(face);
		try {
			const resultado = await clienteAxios.post(
				`/api/upload-image-face/${face.id}`,
				face.image
			);
			console.log(resultado);
			// Insertar el proyecto en el state
			dispatch({
				type: AGREGAR_FOTO,
				payload: resultado.data,
			});
		} catch (error) {
			console.log(error);
		}
	};

	// Selecciona el Proyecto que el usuario dio click
	const faceActual = (faceId) => {
		dispatch({
			type: FACE_ACTUAL,
			payload: faceId,
		});
	};

	// Elimina un proyecto
	const eliminarFace = (faceId) => {
		dispatch({
			type: ELIMINAR_FACE,
			payload: faceId,
		});
	};

	return (
		<faceContext.Provider
			value={{
				proyectos: state.proyectos,
				proyecto: state.proyecto,
				obtenerFaces,
				agregarFace,
				faceActual,
				eliminarFace,
			}}>
			{props.children}
		</faceContext.Provider>
	);
};

export default FaceState;
