import React, {Component} from "react";
import {Dropdown, DropdownToggle, DropdownMenu, DropdownItem} from "reactstrap";
import AuthContext from "../../context/autenticacion/authContext";

//i18n
import {withNamespaces} from "react-i18next";

// users
import avatar2 from "../../../assets/images/users/avatar-2.jpg";

class ProfileMenu extends Component {
	constructor(props) {
		super(props);
		this.state = {
			menu: false,
		};
		this.toggle = this.toggle.bind(this);
	}

	toggle() {
		this.setState((prevState) => ({
			menu: !prevState.menu,
		}));
	}

	render() {
		let username = "Admin";
		if (localStorage.getItem("authUser")) {
			const obj = JSON.parse(localStorage.getItem("authUser"));
			const uNm = obj.email.split("@")[0];
			username = uNm.charAt(0).toUpperCase() + uNm.slice(1);
		}

		const authContext = useContext(AuthContext);
		const {usuarioAutenticado} = authContext;

		useEffect(() => {
			usuarioAutenticado();
		}, []);

		return (
			<React.Fragment>
				<Dropdown
					isOpen={this.state.menu}
					toggle={this.toggle}
					className="d-inline-block user-dropdown">
					<DropdownToggle
						tag="button"
						className="btn header-item waves-effect"
						id="page-header-user-dropdown">
						<img
							className="rounded-circle header-profile-user mr-1"
							src={avatar2}
							alt="Header Avatar"
						/>
						<span className="d-none d-xl-inline-block ml-1 text-transform">
							{username}
						</span>
						<i className="mdi mdi-chevron-down d-none ml-1 d-xl-inline-block"></i>
					</DropdownToggle>
					<DropdownMenu right>
						<DropdownItem href="/profile">
							<i className="ri-user-line align-middle mr-1"></i>{" "}
							{this.props.t("Profile")}
						</DropdownItem>

						<DropdownItem className="d-block" href="/settings">
							<i className="ri-settings-2-line align-middle mr-1"></i>{" "}
							{this.props.t("Settings")}
						</DropdownItem>

						<DropdownItem divider />
						<DropdownItem className="text-danger" href="/logout">
							<i className="ri-shut-down-line align-middle mr-1 text-danger"></i>{" "}
							{this.props.t("Logout")}
						</DropdownItem>
					</DropdownMenu>
				</Dropdown>
			</React.Fragment>
		);
	}
}

export default withNamespaces()(ProfileMenu);
