import {
	REGISTRO_EXITOSO,
	REGISTRO_ERROR,
	OBTENER_USUARIO,
	LOGIN_EXITOSO,
	LOGIN_ERROR,
	ACTUALIZAR_USUARIO,
	CERRAR_SESION,
} from "../../types";

const authReducer = (state, action) => {
	switch (action.type) {
		case REGISTRO_EXITOSO:
		case LOGIN_EXITOSO:
			localStorage.setItem("token", action.payload);
			return {
				...state,
				autenticado: true,
				mensaje: null,
				cargando: false,
			};
		case ACTUALIZAR_USUARIO:
			return {
				...state,
				usuario: state.usuario.map((user) =>
					user._id === action.payload ? action.payload : user
				),
			};
		case OBTENER_USUARIO:
			return {
				...state,
				autenticado: true,
				usuario: action.payload,
				cargando: false,
			};
		case CERRAR_SESION:
		case LOGIN_ERROR:
		case REGISTRO_ERROR:
			localStorage.removeItem("token");
			return {
				...state,
				token: null,
				usuario: null,
				autenticado: null,
				mensaje: action.payload,
				cargando: false,
			};

		default:
			return state;
	}
};

export default authReducer;
