import React, {useContext, useEffect, useState} from "react";
import {
	Row,
	Col,
	Container,
	CardBody,
	Modal,
	CardTitle,
	CardImg,
	Button,
	CardText,
	ModalHeader,
	ModalBody,
	Media,
	FormGroup,
	ModalFooter,
} from "reactstrap";
import {
	AvForm,
	AvField,
	AvInput,
	AvSelect,
	AvRadioGroup,
	AvRadio,
} from "availity-reactstrap-validation";
//Import Images
import img2 from "../../assets/images/small/img-2.jpg";
import avatar1 from "../../assets/images/users/avatar-1.jpg";
import faceContext from "../../context/face/faceContext";

const GetUsers = ({item}) => {
	const [modal, setModal] = useState(false);
	const toggle = () => setModal(!modal);

	/*Creacion del objeto usuario */
	const [usuario, actualizarUsuario] = useState({
		_id: "",
		name: "",
		surname: "",
		age: "",
		gender: "",
		confidenceLevels: "",
		image: "",
	});

	const facesContext = useContext(faceContext);
	const {obtenerImagenFace, imagen} = facesContext;

	useEffect(() => {}, []);

	// Lee los contenidos del input
	const onChange = (e) => {
		actualizarUsuario({
			...usuario,
			[e.target.name]: e.target.value,
		});
	};

	const onSubmitUsuario = (e) => {
		//actualizarFace(usuario);
		toggle();
	};
	{
		/*onClick={() => eliminarUsuario(item._id)}}*/
	}
	return (
		<div className="wrapper">
			<Container>
				<Row className="my-3">
					<Col md="1">
						<div className={"user-img align-self-center mr-3 "}>
							<img
								src="http://localhost:8000/api/get-image-face/${item.name}"
								className="rounded-circle avatar-xs"
								alt=""
							/>
						</div>
					</Col>
					<Col md="6">
						<Media body className="overflow-hidden">
							<h4 className="text-truncate font-size-14 mb-1">
								{item.name} {item.surname}
							</h4>
							<p className="text-truncate mb-0">
								<strong>Edad:</strong> {item.age} - <strong>Sexo:</strong>{" "}
								{item.gender}
							</p>
						</Media>
					</Col>
					<Col md="5">
						<CardText>
							<Button
								color="primary"
								className=" waves-effect waves-light mr-2"
								onClick={toggle}>
								Editar
							</Button>

							<Button
								color="secondary"
								outline
								className=" waves-effect waves-light">
								Eliminar
							</Button>
						</CardText>
					</Col>
				</Row>
				<Modal isOpen={modal} centered={true} toggle={toggle}>
					<ModalHeader toggle={toggle}>
						Editar información de {item.name} {item.surname}
					</ModalHeader>
					<ModalBody>
						{
							<AvForm
								className="needs-validation mt-3"
								onValidSubmit={onSubmitUsuario}>
								<Row>
									<Col md="6">
										<AvField
											name="name"
											label="Nombre"
											value={item.name}
											errorMessage="Introduce un nombre válido"
											className="form-control"
											onChange={onChange}
											validate={{required: {value: true}}}
										/>
									</Col>
									<Col md="6">
										<AvField
											name="surname"
											label="Apellido"
											value={item.surname}
											errorMessage="Introduce un apellido válido"
											onChange={onChange}
											className="form-control"
											validate={{required: {value: true}}}
										/>
									</Col>
								</Row>
								<AvField
									name="age"
									label="Edad"
									type="text"
									value={item.age}
									validate={{number: true}}
									errorMessage="Introduce una edad válida"
									className="form-control"
									onChange={onChange}
									required
								/>
								<AvRadioGroup
									inline
									label="Sexo"
									name="gender"
									required
									errorMessage="Selecciona el sexo">
									<AvRadio
										label="Masculino"
										value="Masculino"
										onChange={onChange}
									/>
									<AvRadio
										label="Femenino"
										value="Femenino"
										onChange={onChange}
									/>
									<AvRadio label="Otro" value="Otro" onChange={onChange} />
								</AvRadioGroup>

								<AvField
									type="select"
									required
									value={item.confidenceLevels}
									label="Nivel de confianza"
									onChange={onChange}
									name="confidenceLevels"
									errorMessage="Selecciona el nivel de confianza al que pertenece el usuario">
									<option value="">Selecciona un nivel de confianza</option>
									<option value="60316459aea69a7200c32a64">1</option>
									<option value="603165309fc13d17bcbd7089">2</option>
									<option value="603167ef9fc13d17bcbd708a">3</option>
									<option value="603168839fc13d17bcbd708b">4</option>
								</AvField>
								<AvField
									type="file"
									id="image"
									name="image"
									errorMessage="La foto del rostro del usuario es obligatoria"
									onChange={onChange}
								/>

								<FormGroup>
									<Button
										color="primary"
										className=" waves-effect waves-light mr-2">
										Añadir
									</Button>
								</FormGroup>
							</AvForm>
						}
					</ModalBody>
				</Modal>
			</Container>
		</div>
	);
};

export default GetUsers;
