import React, {Component} from "react";
import {Row, Col, Button, Alert, Container, Label, FormGroup} from "reactstrap";

// availity-reactstrap-validation
import {AvForm, AvField} from "availity-reactstrap-validation";

// action
import {registerUser, registerUserFailed, apiError} from "../../store/actions";

// Redux
import {connect} from "react-redux";
import {Link} from "react-router-dom";

class Register extends Component {
	constructor(props) {
		super(props);
		this.state = {
			email: "",
			firstname: "",
			lastname: "",
			password: "",
		};
		this.handleSubmit = this.handleSubmit.bind(this);
	}

	handleSubmit(event, values) {
		this.props.registerUser(values);
	}

	componentDidMount() {
		this.props.registerUserFailed("");
		this.props.apiError("");
		document.body.classList.add("auth-body-bg");
	}

	render() {
		return (
			<React.Fragment>
				<div>
					<Container fluid className="p-0">
						<Row className="no-gutters">
							<Col lg={4}>
								<div className="authentication-page-content p-4 d-flex align-items-center min-vh-100">
									<div className="w-100">
										<Row className="justify-content-center">
											<Col lg={9}>
												<div>
													<div className="text-center">
														<h4 className="font-size-18 mt-4">Regístrate</h4>
													</div>

													{this.props.user && (
														<Alert color="success">
															Registration Done Successfully.
														</Alert>
													)}

													{this.props.registrationError && (
														<Alert color="danger">
															{this.props.registrationError}
														</Alert>
													)}

													<div className="p-2 mt-5">
														<AvForm
															onValidSubmit={this.handleSubmit}
															className="form-horizontal">
															<FormGroup className="auth-form-group-custom mb-4">
																<i className="ri-mail-line auti-custom-input-icon"></i>
																<Label htmlFor="userfirstname">Nombre</Label>
																<AvField
																	name="firstname"
																	value={this.state.email}
																	validate={{firstname: true, required: true}}
																	type="text"
																	className="form-control"
																	id="userfirstname"
																	placeholder="Introduce tu nombre"
																/>
															</FormGroup>

															<FormGroup className="auth-form-group-custom mb-4">
																<i className="ri-mail-line auti-custom-input-icon"></i>
																<Label htmlFor="userlastname">Apellido</Label>
																<AvField
																	name="lastname"
																	value={this.state.email}
																	validate={{lastname: true, required: true}}
																	type="text"
																	className="form-control"
																	id="userlastname"
																	placeholder="Introduce tu nombre"
																/>
															</FormGroup>

															<FormGroup className="auth-form-group-custom mb-4">
																<i className="ri-mail-line auti-custom-input-icon"></i>
																<Label htmlFor="useremail">Email</Label>
																<AvField
																	name="email"
																	value={this.state.email}
																	validate={{email: true, required: true}}
																	type="email"
																	className="form-control"
																	id="useremail"
																	placeholder="Introduce tu correo electrónico"
																/>
															</FormGroup>

															<FormGroup className="auth-form-group-custom mb-4">
																<i className="ri-lock-2-line auti-custom-input-icon"></i>
																<Label htmlFor="userpassword">Contraseña</Label>
																<AvField
																	name="password"
																	value={this.state.password}
																	type="password"
																	className="form-control"
																	id="userpassword"
																	placeholder="Introduce tu contraseña"
																/>
															</FormGroup>

															<div className="text-center">
																<Button
																	color="primary"
																	className="w-md waves-effect waves-light"
																	type="submit">
																	{this.props.loading
																		? "Loading ..."
																		: "Registrarse"}
																</Button>
															</div>

															<div className="mt-4 text-center">
																<p className="mb-0">
																	Al registrarse aceptas nuestros términos y
																	condiciones{" "}
																	<Link to="#" className="text-primary">
																		Términos y condiciones
																	</Link>
																</p>
															</div>
														</AvForm>
													</div>

													<div className="mt-5 text-center">
														<p>
															Ya tienes una cuenta?{" "}
															<Link
																to="/login"
																className="font-weight-medium text-primary">
																{" "}
																Iniciar sesión
															</Link>{" "}
														</p>
													</div>
												</div>
											</Col>
										</Row>
									</div>
								</div>
							</Col>
							<Col lg={8}>
								<div className="authentication-bg">
									<div className="bg-overlay"></div>
								</div>
							</Col>
						</Row>
					</Container>
				</div>
			</React.Fragment>
		);
	}
}

const mapStatetoProps = (state) => {
	const {user, registrationError, loading} = state.Account;
	return {user, registrationError, loading};
};

export default connect(mapStatetoProps, {
	registerUser,
	apiError,
	registerUserFailed,
})(Register);
