import {
	OBTENER_FACES,
	AGREGAR_FACE,
	FACE_ACTUAL,
	AGREGAR_FOTO,
	ELIMINAR_FACE,
	OBTENER_FACE,
	OBTENER_FACE_FILTRADO,
	ACTUALIZAR_FACE,
	OBTENER_FACE_IMAGEN,
} from "../../types";

export default (state, action) => {
	switch (action.type) {
		case OBTENER_FACE_FILTRADO:
			return {
				...state,
				faces_filtrados: action.payload,
			};
		case OBTENER_FACE_IMAGEN:
			return {
				...state,
				imagen: action.payload,
			};
		case OBTENER_FACES:
			return {
				...state,
				faces: action.payload,
			};
		case OBTENER_FACE:
			return {
				...state,
				faces: action.payload,
			};
		case AGREGAR_FOTO:
		case AGREGAR_FACE:
			return {
				...state,
				faces: [...state.faces, action.payload],
			};
		case ACTUALIZAR_FACE:
			return {
				...state,
				faces: state.faces.map((face) =>
					face._id === action.payload._id ? action.payload : face
				),
			};
		case FACE_ACTUAL:
			return {
				...state,
				faces: [...state.faces, action.payload],
			};
		case ELIMINAR_FACE:
			return {
				...state,
				faces: state.tareasproyecto.filter(
					(tarea) => tarea._id !== action.payload
				),
			};
		default:
			return state;
	}
};
