import React, {useContext, useEffect, useState} from "react";
import {
	FormGroup,
	Label,
	Input,
	FormText,
	Row,
	Col,
	Button,
	ModalHeader,
	ModalBody,
	ModalFooter,
} from "reactstrap";
import {
	AvForm,
	AvField,
	AvInput,
	AvSelect,
	AvRadioGroup,
	AvRadio,
} from "availity-reactstrap-validation";
import faceContext from "../../context/face/faceContext";

const AddUser = ({toggle}) => {
	const facesContext = useContext(faceContext);
	const {agregarFace} = facesContext;

	/*Creacion del objeto usuario */
	const [usuario, guardarUsuario] = useState({
		name: "",
		surname: "",
		age: "",
		gender: "",
		confidenceLevels: "",
		image: "",
	});

	// Lee los contenidos del input
	const onChange = (e) => {
		guardarUsuario({
			...usuario,
			[e.target.name]: e.target.value,
		});
	};

	const onSubmitProyecto = (e) => {
		agregarFace(usuario);
		//Reiniciar formulario
		guardarUsuario({
			name: "",
			surname: "",
			age: "",
			gender: "",
			confidenceLevels: "",
			image: "",
		});
		toggle();
	};

	return (
		<React.Fragment>
			<ModalHeader toggle={toggle}>AÃ±adir usuarios</ModalHeader>
			<ModalBody></ModalBody>
		</React.Fragment>
	);
};

export default AddUser;
