import React, {useState, useContext, useEffect} from "react";
import {Dropdown, DropdownToggle, DropdownMenu, DropdownItem} from "reactstrap";
import AuthContext from "../../../context/autenticacion/authContext";

//i18n
import {withNamespaces} from "react-i18next";

// users
import avatar2 from "../../../assets/images/users/avatar-2.jpg";

/*
	constructor(props) {
		super(props);
		this.state = {
			menu: false,
		};
		this.toggle = this.toggle.bind(this);
	}

	toggle() {
		this.setState((prevState) => ({
			menu: !prevState.menu,
		}));
	}
*/

const ProfileMenu = () => {
	const authContext = useContext(AuthContext);
	const {usuario, usuarioAutenticado} = authContext;
	const [togglevar, setToggle] = useState(false);

	const toggle = () => {
		toggle = toggle.bind(this);
		setToggle(!togglevar);
	};

	useEffect(() => {
		usuarioAutenticado();
	}, []);

	return (
		<React.Fragment>
			<Dropdown className="d-inline-block user-dropdown">
				<DropdownToggle
					tag="button"
					className="btn header-item waves-effect"
					id="page-header-user-dropdown">
					<img
						className="rounded-circle header-profile-user mr-1"
						src={avatar2}
						alt="Header Avatar"
					/>
					<span className="d-none d-xl-inline-block ml-1 text-transform">
						{usuario ? usuario.name : null}
					</span>
					<i className="mdi mdi-chevron-down d-none ml-1 d-xl-inline-block"></i>
				</DropdownToggle>
				<DropdownMenu right>
					<DropdownItem href="/profile">
						<i className="ri-user-line align-middle mr-1"></i> Profile
					</DropdownItem>

					<DropdownItem className="d-block" href="/settings">
						<i className="ri-settings-2-line align-middle mr-1"></i> Settings
					</DropdownItem>

					<DropdownItem divider />
					<DropdownItem className="text-danger" href="/logout">
						<i className="ri-shut-down-line align-middle mr-1 text-danger"></i>{" "}
						Logout
					</DropdownItem>
				</DropdownMenu>
			</Dropdown>
		</React.Fragment>
	);
};

export default ProfileMenu;
